import './App.css';
import Chart from './Chart.js';
import {useState, useEffect} from 'react';
import Artists from './Artists.js';
import Favs from './Favs.js';
import mtvCSV from './data/mtv.csv'
import grammysJSON from './data/dataGrammys.json';

// create array of unique artist names
const artistArray = [...new Set(grammysJSON.map(item => item['ART-1']))];
grammysJSON.forEach(function (award) {
	if (!artistArray.includes(award['ART-2'])) {return award['ART-2']}
}); 

// make array of objects - each obj an artist (used for drop down options)
const artistOptions = artistArray.map((artist) => { return {text: artist, value: artist} }).sort();
console.log(artistOptions);

// App component
export default function App() {
  const [artist, setArtist] = useState('');
  const [favs, setFavs] = useState(['Bruno Mars']);
  const [grammysData, setgrammysData] = useState([...grammysJSON]);
  const [mtvData, setMtvData] = useState([]);

  const handleArtistChange = (artist) => {
    setArtist(artist);
  }

  const handleFavArtist = () => {
    let currFavs = favs;
    if (!favs.includes(artist)) {
      currFavs.push(artist);
      setFavs([...currFavs]);
    }
  }

  const pickedFavs = favs.map((item, index)=>{
    return <Favs artist={item}></Favs>
  })

  return (
    <div className="App">
      <h1>Final Project</h1>
      <h2>Awards for {artist}</h2>
      <button onClick={handleFavArtist}>❤️</button>
      <Artists options={artistOptions} onArtistChange={handleArtistChange}/>
      <div className="App">
        <span>Favorite artists:</span> 
        {pickedFavs}
        <p><span><i>click to remove</i></span></p>
      </div>
      <Chart data={grammysData} height={150} width={960} show={"Grammys"} artist={artist} />
      {/* <Chart data={mtvData} height={150} width={960} show={"MTV"} artist={artist} /> */}
    </div>
  );
}
