import './App.css';
import Chart from './Chart.js';
import {useState, useEffect} from 'react';
import Artists from './Artists.js';
import Favs from './Favs.js';
import grammysJSON from './data/dataGrammys.json';
import mtvJSON from './data/dataMTV.json';

const grammysAwards = [...new Set(grammysJSON.map(item => item['AWARD']))];

// create array of unique artist names
let artistArray = [...new Set(grammysJSON.map(item => item['ART-1']))];
grammysJSON.forEach(function (award) {
	if (!artistArray.includes(award['ART-2'])) {artistArray.push(award['ART-2'])}
  if (!artistArray.includes(award['ART-3'])) {artistArray.push(award['ART-3'])}
}); 
mtvJSON.forEach(function (award) {
	if (!artistArray.includes(award['ART-1'])) {artistArray.push(award['ART-1'])}
  if (!artistArray.includes(award['ART-2'])) {artistArray.push(award['ART-2'])}
}); 
console.log(artistArray);

// filter out any empty artists
artistArray = artistArray.filter(item => {
  return item !== ""
})
// sort artist array in alpa order
artistArray = artistArray.sort();

// make array of objects - each obj is an artist (used for drop down options)
const artistOptions = artistArray.map((artist) => { return {text: artist, value: artist} });

// App component
export default function App() {
  const [artist, setArtist] = useState('(pick an artist)');
  const [favs, setFavs] = useState(['Bruno Mars']);
  const [grammysData, setgrammysData] = useState([...grammysJSON]);
  const [mtvData, setMtvData] = useState([]);

  const handleArtistChange = (artist) => {
    setArtist(artist);
  }

  const handleFavArtist = () => {
    let currFavs = favs;
    if (!favs.includes(artist)) {
      currFavs.push(artist);
      setFavs([...currFavs]);
    }
  }

  const pickedFavs = favs.map((item, index)=>{
    return <Favs artist={item}></Favs>
  })

  const artistText = () => {
    if (artist === '') {
      return 'Choose an artist to see the awards theyve won';
    } else {
      return 'Awards for ${artist}';
    }
  }

  return (
    <div className="App">
      <h1>Final Project</h1>
      <h2>{artistText()}</h2>
      <button onClick={handleFavArtist}>❤️</button>
      <Artists options={artistOptions} onArtistChange={handleArtistChange}/>
      <div className="App">
        <span>Favorite artists:</span> 
        {pickedFavs}
        <p><span><i>click to remove</i></span></p>
      </div>
      <Chart data={grammysData} awards={grammysAwards} height={150} width={960} show={"Grammys"} artist={artist} />
      {/* <Chart data={mtvData} height={150} width={960} show={"MTV"} artist={artist} /> */}
    </div>
  );
}
