import React from 'react';
import * as d3 from 'd3';
import { useD3 } from './useD3.js';

export default function Chart(props) {

    const ref = useD3(
        (svg) => {
            const height = props.height;
            const width = props.width;
            const margin = {top: 1, right: 15, bottom: 20, left: 125};

            const parseTime = d3.timeParse("%Y");
            props.data.map(d => { return {'YEAR' : parseTime(d.YEAR)} });

            const x = d3.scaleTime()
                .rangeRound([margin.right, width-margin.right-margin.left])
                .domain(d3.extent(props.data, function(d) { return parseTime(d.YEAR); }));

            const y = d3.scaleBand()
                .domain(['RECORD OF THE YEAR', 'ALBUM OF THE YEAR', 'SONG OF THE YEAR', 'BEST NEW ARTIST'])
                .rangeRound([0, height-margin.top-margin.bottom])
                .padding(1);

            const xAxis = g => g
                .attr("transform", `translate(${margin.left},${height - margin.bottom})`)
                .call(d3.axisBottom(x));

            const yAxis = g => g
                .attr("transform", `translate(${margin.left},${margin.top})`)
                .call(d3.axisLeft(y));

            svg.select(".x-axis").call(xAxis);
            svg.select(".y-axis").call(yAxis);

            svg
                .select(".plot")
                .attr("transform", `translate(${margin.left},${margin.top})`)
                .selectAll("dot")
                .data(props.data)
                .enter()
                .append("circle")
                .attr("class", (d)=>{ if(d.WINNER !== ""){return "circle"}else{return "remove-circle"} })
                .attr("cy", d => y(d.AWARD) )
                .attr("cx", d => x(parseTime(d.YEAR)) )
                .attr("i", (d,i) => i )
                .attr("artist", d => d['ART1'].split(" ").join(""))
                .attr("artist2", d => {if(d['ART2']) {return d['ART2'].split(" ").join("")}})
                .attr("r", 5.5)
                .attr("stroke-width",2)
                .attr("stroke", "#fff")
                .on("mouseover",function(d) {
                    let index = d3.select(this).attr("i"); 
                    let cynum = d3.select(this).attr("cy");
                    let cxnum = d3.select(this).attr("cx");
                    let side;
                    if (index > props.data.length/2) {side = 'left';} else {side = 'right';}
                    d3.select(this).attr("stroke", "#666");
                    d3.select("text.tooltip-artist")
                        .text(() => {return props.data[index]['ARTIST']+', '+ props.data[index]['WINNER']})
                        .attr("dy", () => {return +cynum})
                        .attr("dx", () => {if (side == "left") {return (+cxnum + 8)} else {return (+cxnum - 9)} })
                        .attr("text-anchor", () => { if (side == "left") {return "start"} else {return "end"} })
                        .style("opacity", 1);
                })
                .on("mouseout",function(d,i) {
                    d3.selectAll("circle.circle").attr("stroke", "#fff");
                    d3.select("text.tooltip-artist").style("opacity", 0);
                });

            svg
                .select(".plot")
                .append("text")
                .attr("class", "tooltip-artist")
                .style("pointer-events", "none")
                .style("font-size", "14px")
                .style("font-weight", "bold")
                .style("opacity", 0);
        }, 
        [props.data.length]
    );

    return(
        <svg
            ref={ref}
            style={{
                height: props.height,
                width: props.width
            }}
        >
            <g className='plot' />
            <g className='x-axis' />
            <g className='y-axis' />
        </svg>
    )

}
