import './App.css';
import Chart from './Chart.js';
import {useState} from 'react';
import Artists from './Artists.js';

const data = [
  {YEAR: "2020", AWARD: "RECORD OF THE YEAR", WINNER: "Everything I Wanted", ARTIST: "Billie Eilish", ART1: "Billie Eilish", ART2: ""},
  {YEAR: "2020", AWARD: "ALBUM OF THE YEAR", WINNER: "Folklore", ARTIST: "Taylor Swift", ART1: "Taylor Swift", ART2: ""},
  {YEAR: "2020", AWARD: "SONG OF THE YEAR", WINNER: "I Can't Breathe", ARTIST: "Dernst Emile II, H.E.R. & Tiara Thomas", ART1: "Dernst Emile II, H.E.R. & Tiara Thomas", ART2: ""},
  {YEAR: "2020", AWARD: "BEST NEW ARTIST", WINNER: "", ARTIST: "Megan Thee Stallion", ART1: "Megan Thee Stallion", ART2: ""},
  {YEAR: "2019", AWARD: "RECORD OF THE YEAR", WINNER: "Bad Guy", ARTIST: "Billie Eilish", ART1: "Billie Eilish", ART2: ""},
  {YEAR: "2019", AWARD: "ALBUM OF THE YEAR", WINNER: "When We All Fall Asleep, Where Do We Go?", ARTIST: "Billie Eilish", ART1: "Billie Eilish", ART2: ""},
  {YEAR: "2019", AWARD: "SONG OF THE YEAR", WINNER: "Bad Guy", ARTIST: "Billie Eilish", ART1: "Billie Eilish", ART2: ""},
  {YEAR: "2019", AWARD: "BEST NEW ARTIST", WINNER: "", ARTIST: "Billie Eilish", ART1: "Billie Eilish", ART2: ""},
  {YEAR: "2018", AWARD: "RECORD OF THE YEAR", WINNER: "This Is America", ARTIST: "Childish Gambino", ART1: "Childish Gambino", ART2: ""},
  {YEAR: "2018", AWARD: "ALBUM OF THE YEAR", WINNER: "Golden Hour", ARTIST: "Kacey Musgraves", ART1: "Kacey Musgraves", ART2: ""},
  {YEAR: "2018", AWARD: "SONG OF THE YEAR", WINNER: "This Is America", ARTIST: "Childish Gambino", ART1: "Childish Gambino", ART2: ""},
  {YEAR: "2018", AWARD: "BEST NEW ARTIST", WINNER: "", ARTIST: "Dua Lipa", ART1: "Dua Lipa", ART2: ""},
  {YEAR: "2017", AWARD: "RECORD OF THE YEAR", WINNER: "24K Magic", ARTIST: "Bruno Mars", ART1: "Bruno Mars", ART2: ""},
  {YEAR: "2017", AWARD: "ALBUM OF THE YEAR", WINNER: "24K Magic", ARTIST: "Bruno Mars", ART1: "Bruno Mars", ART2: ""},
  {YEAR: "2017", AWARD: "SONG OF THE YEAR", WINNER: "That's What I Like", ARTIST: "Bruno Mars", ART1: "Bruno Mars", ART2: ""},
  {YEAR: "2017", AWARD: "BEST NEW ARTIST", WINNER: "", ARTIST: "Alessia Cara", ART1: "Alessia Cara", ART2: ""},
  {YEAR: "2016", AWARD: "RECORD OF THE YEAR", WINNER: "Hello", ARTIST: "Adele", ART1: "Adele", ART2: ""},
  {YEAR: "2016", AWARD: "ALBUM OF THE YEAR", WINNER: "25", ARTIST: "Adele", ART1: "Adele", ART2: ""},
  {YEAR: "2016", AWARD: "SONG OF THE YEAR", WINNER: "Hello", ARTIST: "Adele", ART1: "Adele", ART2: ""},
  {YEAR: "2016", AWARD: "BEST NEW ARTIST", WINNER: "", ARTIST: "Chance The Rapper", ART1: "Chance The Rapper", ART2: ""}
];

const artistOptions = [
  {
    text: 'Bruno Mars',
    value: 'Bruno Mars'
  },
  {
    text: 'Billie Eilish',
    value: 'Billie Eilish'
  }
]

export default function App() {
  const [artist, setArtist] = useState('Bruno Mars');

  const handleArtistChange = (artist) => {
    setArtist(artist);
  }

  return (
    <div className="App">
      <h1>Final Project</h1>
      <h2>Awards for {artist}</h2>
      <Artists options={artistOptions} onArtistChange={handleArtistChange}/>
      <Chart data={data} height={150} width={960} show={"Grammys"} artist={artist} />
    </div>
  );
}
