import './App.css';
import Chart from './Chart.js';
import {useState} from 'react';
import Artists from './Artists.js';
import Favs from './Favs.js';
import grammysJSON from './data/dataGrammys.json';
import mtvJSON from './data/dataMTV.json';
import billboardJSON from './data/dataBillboard.json';
import amaJSON from './data/dataAMA.json';
import superbowlJSON from './data/dataSuperBowl.json';
import { Markup } from 'interweave';

// award names for each award show
const grammysAwards = [...new Set(grammysJSON.map(item => item['AWARD']))];
const mtvAwards = [...new Set(mtvJSON.map(item => item['AWARD']))];
const billboardAwards = [...new Set(billboardJSON.map(item => item['AWARD']))];
const amaAwards = [...new Set(amaJSON.map(item => item['AWARD']))];

// get artist names for drop down:
// create array of unique artist names
let artistArray = [...new Set(grammysJSON.map(item => item['ART-1']))];
grammysJSON.forEach(function (award) {
	if (!artistArray.includes(award['ART-2'])) {artistArray.push(award['ART-2'])}
  if (!artistArray.includes(award['ART-3'])) {artistArray.push(award['ART-3'])}
}); 
mtvJSON.forEach(function (award) {
	if (!artistArray.includes(award['ART-1'])) {artistArray.push(award['ART-1'])}
  if (!artistArray.includes(award['ART-2'])) {artistArray.push(award['ART-2'])}
  if (!artistArray.includes(award['ART-3'])) {artistArray.push(award['ART-3'])}
});
billboardJSON.forEach(function (award) {
	if (!artistArray.includes(award['ART-1'])) {artistArray.push(award['ART-1'])}
  if (!artistArray.includes(award['ART-2'])) {artistArray.push(award['ART-2'])}
  if (!artistArray.includes(award['ART-3'])) {artistArray.push(award['ART-3'])}
});
amaJSON.forEach(function (award) {
	if (!artistArray.includes(award['ART-1'])) {artistArray.push(award['ART-1'])}
});
// filter out any empty artists
artistArray = artistArray.filter(item => {
  return item !== ""
})
// sort artist array in alpa order
artistArray = artistArray.sort();
// make array of objects - each obj is an artist (used for drop down options)
const artistOptions = artistArray.map((artist) => { return {text: artist, value: artist} });



export default function App() {
  const [artist, setArtist] = useState('none');
  const [favs, setFavs] = useState(['Billie Eilish', 'Taylor Swift', 'Alicia Keys', 'BeyoncÃ©']);
  const [grammysCountArtist, setgrammysCountArtist] = useState(0);
  const [mtvCountArtist, setmtvCountArtist] = useState(0);
  const [billboardCountArtist, setbillboardCountArtist] = useState(0);
  const [amaCountArtist, setamaCountArtist] = useState(0);
  const [artistSummary, setartistSummary] = useState('Choose an artist to see their summary');

  const handleArtistSelection = (artist) => {
    setArtist(artist);
    getArtistImage(artist);

    let grammyCount = getArtistCount(grammysJSON, artist);
    setgrammysCountArtist(grammyCount);

    let mtvCount = getArtistCount(mtvJSON, artist);
    setmtvCountArtist(mtvCount);

    let billboardCount = getArtistCount(billboardJSON, artist);
    setbillboardCountArtist(billboardCount);
  }

  function getArtistCount(data, artist) {
    let count = 0;
    for (let k of data) {
      if (k['ART-1'] == artist) { count++; }
      if (k['ART-2'] == artist) { count++; }
      if (k['ART-3'] == artist) { count++; }
    };
    return count;
  }

  const lastfm = {
    api_key: 'a36b582801c6e46141b9896f2b65b1c4'
  }

  function getArtistImage(artist) {
    const url = `http://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=${artist}&api_key=${lastfm.api_key}&format=json`;
    fetch(url)
      .then((response) => {
        return response.json();
      }).then((data) => {
        setartistSummary(data.artist.bio.summary);
      });
  }

  const handleFavArtist = (add, theartist) => {
    let currFavs = favs;
    if (!favs.includes(theartist) && add) {
      currFavs.push(theartist);
      setFavs([...currFavs]);
    } else if (favs.includes(theartist)) {
      let index = currFavs.indexOf(theartist);
      currFavs.splice(index, 1);
      setFavs([...currFavs]);
    }
  }

  const pickedFavs = favs.map((item, index)=>{
    return <Favs artist={item} artistSelection={handleArtistSelection} handleAristRemove={handleFavArtist}></Favs>
  })

  const artistText = () => {
    if (artist === 'none') {
      return 'Choose an artist to see their award(s)';
    } else {
      return `Awards for ${artist}`;
    }
  }

  return (
    <div className="App">
      <h1>Explore Music Award Show Winners</h1>
      <Artists options={artistOptions} onArtistChange={handleArtistSelection}/>
      <button onClick={() => {handleFavArtist(true, artist)}}>â¤ï¸</button>
      <hr />
      <h5>Artist Summary:</h5>
      <p><Markup content={artistSummary}/></p>
      <hr />
      <div>
        <span>Favorite artists:</span> 
        {pickedFavs}
      </div>
      <hr />
      <h3>{artistText()}</h3>
      <Chart data={grammysJSON} awards={grammysAwards} awardCountForArist={grammysCountArtist} id={'grammy'} height={130} width={960} show={"Grammys"} artist={artist} />
      <Chart data={mtvJSON} awards={mtvAwards} awardCountForArist={mtvCountArtist} id={'mtv'} height={170} width={960} show={"MTV Video Music Awards"} artist={artist} />
      <Chart data={billboardJSON} awards={billboardAwards} awardCountForArist={billboardCountArtist}  id={'billboard'} height={170} width={960} show={"Billboard Music Awards"} artist={artist} />
      <Chart data={amaJSON} awards={amaAwards} awardCountForArist={billboardCountArtist}  id={'billboard'} height={170} width={960} show={"Billboard Music Awards"} artist={artist} />
    </div>
  );
}
