import React from 'react';
import * as d3 from 'd3';
import { useD3 } from './useD3.js';

export default function Chart(props) {

    const ref = useD3(
        (svg) => {
            const height = 500;
            const width = 500;
            const margin = { top: 20, right: 30, bottom: 30, left: 40 };
        }, 
        [props.data.length]
    );

    return(
        <svg
            ref={ref}
            style={{
                height: 300,
                width: 700
            }}
        >
            <g className='plot' />
            <g className='x-axis' />
            <g className='y-axis' />
        </svg>
    )

}
