import React from 'react';

export default function Favs(props) {

    const handleClick = () => {
        props.artistSelection(props.artist);
    }

    const handleRemove = () => {
        props.handleAristRemove(false, props.artist);
    }

    return(
        <div>
            <button className='btn btn-light btn-outline-secondary' onClick={handleClick}>{props.artist}</button>
            <button className='btn btn-light btn-outline-secondary' onClick={handleRemove}>X</button>
        </div>
    )
}
