import React from 'react';
import {Dropdown} from 'semantic-ui-react'

export default function Artists (props) {

    const handleOnChange = (e, data) => {
        props.onArtistChange(data.value, false);
    }

    return(
        <Dropdown placeholder='pick an artist' search selection options={props.options} onChange={handleOnChange} />
    )
}
